import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../pages/InfoPost.css'
import { supabase } from '../client'

const InfoPost = ({ data }) => {
    const {id} = useParams()
    const [post, setPost] = useState({id: null, title: "", content: "", link: ""});

    useEffect(() => {
      const fetchData = async () => {
          const { data } = await supabase
            .from('SportsTalk')
            .select()
            .eq('id', id)
            .single(); // Assuming you expect only one row to match the ID


          setPost(data);
      };

      fetchData();
    }, []);

    return (
      <div className="crewmate-container">
        <p>{post.created_at}</p>
        <h1>Crewmate: {post.title}</h1>
        <p>Speed: {post.content} </p>
        <p>Upvotes: {post.upvotes}</p>
      </div>
    );
  };

  export default InfoPost;
