import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../pages/InfoPost.css'
import { supabase } from '../client'

const InfoPost = (props) => {
    const { id } = useParams()
    const [post, setPost] = useState({ id: null, title: "", content: "", link: "" });
    const [hoursAgo, setHoursAgo] = useState(null)
    const [count, setCount] = useState(0)

    useEffect(() => {
        const fetchData = async () => {
            try {
                const { data } = await supabase
                    .from('SportsTalk')
                    .select()
                    .eq('id', id)
                    .single();

                setPost(data);

                const createdAt = new Date(data.created_at);
                const currentTime = new Date();
                const hoursDifference = Math.abs(currentTime - createdAt) / 36e5;
                setHoursAgo(Math.round(hoursDifference));
            } catch (error) {
                console.error('Error fetching data:', error.message);
            }
        };

        fetchData();
    }, [id]);

    const updateCount = async (event) => {
      event.preventDefault()

      await supabase 
        .from('SportsTalk')
        .update({ upvotes: post.upvotes + 1 })
        .eq('id', id)

      setPost((prevState) => ({
        ...prevState
      }))
    }

    return (
        <div className="crewmate-container">
            <h5>Posted {hoursAgo} hours ago</h5>
            <h5>{post.title}</h5>
            <h5>{post.content} </h5>
            <img src={post.link} alt="post image" />
            <button className="upvoteButton" onClick={updateCount}>üëç</button>
            <h5>{count} upvotes</h5>
        </div>
    );
};

export default InfoPost;
