import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import '../pages/EditPost.css'
import { supabase } from '../client'

const EditPost = ({data}) => {

    const {id} = useParams();
    const [post, setPost] = useState({id: null, name: "", speed: "", color: ""});

    const updatePost = async (event) => {
        event.preventDefault()

        await supabase
            .from('SportsTalk')
            .update({title: post.title, content: post.content, link: post.link})
            .eq('id', id)

            window.location = "/"
    }

    const deletePost = async (event) => {
        event.preventDefault()

        await supabase
            .from('SportsTalk')
            .delete()
            .eq('id', id)

        window.location = "http://localhost:3000/"
    }

    const handleChange = (event) => {
        const {name, value} = event.target;
        setPost( (prev) => {
            return {
                ...prev,
                [name]:value,
            }
        })
    }

    return (
        <div>
            <form>
                <label for="name">Name</label> <br />
                <input type="text" id="title" name="title" placeholder="Enter crewmate's name" value={post.title} onChange={handleChange} /><br />
                <br/>

                <label for="speed">Speed</label><br />
                <input type="text" id="speed" name="speed" placeholder="Content (Optional)" value={post.content} onChange={handleChange} /><br />
                <br/>

                <br/>
                <input type="submit" value="Update Crewmate" onClick={updatePost} />
                <button className="deleteButton" onClick={deletePost}>Delete Post</button>
            </form>
        </div>
    )
}

export default EditPost
