import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../pages/InfoPost.css'
import { supabase } from '../client'
import { Link } from 'react-router-dom'
import thumbsup from './thumbs-up.png'
import deletepost from './delete.png'
import editpost from './edit.png'

const InfoPost = (props) => {
    const { id } = useParams()
    const [post, setPost] = useState({ id: null, title: "", content: "", link: "" });
    const [hoursAgo, setHoursAgo] = useState(null)
    const [count, setCount] = useState(0)

    useEffect(() => {
        const fetchData = async () => {
            try {
                const { data } = await supabase
                    .from('SportsTalk')
                    .select()
                    .eq('id', id)
                    .single();

                setPost(data);

                const createdAt = new Date(data.created_at);
                const currentTime = new Date();
                const hoursDifference = Math.abs(currentTime - createdAt) / 36e5;
                setHoursAgo(Math.round(hoursDifference));
            } catch (error) {
                console.error('Error fetching data:', error.message);
            }
        };

        fetchData();
    }, [id]);

    const updateCount = async (event) => {
      event.preventDefault()

      const {data: postInDB} = await supabase
        .from('SportsTalk')
        .select('upvotes')
        .eq('id', id)
        .single()

      await supabase 
        .from('SportsTalk')
        .update({ upvotes: postInDB.upvotes + 1 })
        .eq('id', id)

      setPost((prevState) => ({
        ...prevState,
        upvotes: postInDB.upvotes + 1
      }))

      setCount((prevCount) => prevCount + 1)
    }

    const deletePost = async (event) => {
      event.preventDefault()

      await supabase
          .from('SportsTalk')
          .delete()
          .eq('id', id)

      window.location = "http://localhost:3000/"
  }

    return (
        <div className="Card">
            <h5>Posted {hoursAgo} hours ago</h5>
            <h5>{post.title}</h5>
            <h5>{post.content} </h5>
            <img src={post.link} alt="post image" /> <br/>
            <div className="button-container">
              <div className="upvote-container">
                <button 
                  className="upvoteButton" 
                  onClick={updateCount}>
                    <img src={thumbsup} alt="thumbs up" className="thumbs-up" />
                </button>
                <span>{post.upvotes} upvotes</span>
              </div>

              <div className="edit-del">
                <Link to={'./edit/' + post.id}>
                  <img src={editpost} className="edit-button"/>
                </Link>
              </div>
            </div>
        </div>
    );
};

export default InfoPost;
