import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import '../pages/EditPost.css'
import { supabase } from '../client'

const EditPost = ({data}) => {

    const {id} = useParams();
    const [post, setPost] = useState({id: null, name: "", speed: "", color: ""});

    const updatePost = async (event) => {
        event.preventDefault()

        await supabase
            .from('SportsTalk')
            .update({title: post.title, content: post.content, link: post.link})
            .eq('id', id)

            window.location = "/"
    }

    const deletePost = async (event) => {
        event.preventDefault()

        await supabase
            .from('SportsTalk')
            .delete()
            .eq('id', id)

        window.location = "http://localhost:3000/"
    }

    const handleChange = (event) => {
        const {name, value} = event.target;
        setPost( (prev) => {
            return {
                ...prev,
                [name]:value,
            }
        })
    }

    return (
        <div>
            <form className="create-form">
                <input type="text" id="title" name="title" placeholder="Title" onChange={handleChange} /><br />

                <textarea rows="5" cols="50" id="content" name="content" placeholder="Content (Optional)" onChange={handleChange}>
                </textarea><br />

                <input type="text" id="link" name="link" placeholder="Image URL (Optional)" onChange={handleChange} /><br />

                <input 
                    type="submit" 
                    value="Create Post" 
                    onClick={createPost}
                    style={{backgroundColor: '#FEAA5E', color: 'white'}}
                />
            </form>
        </div>
    )
}

export default EditPost
