import React, { useState, useEffect } from 'react'
import './Card.css'
import more from './more.png'
import { Link } from 'react-router-dom'
import { supabase } from '../client' 
import { useParams } from 'react-router-dom';


const Card = (props) =>  {

  const { id } = useParams()
  const [post, setPost] = useState({ id: null, title: "", content: "", link: ""})
  const [hoursAgo, setHoursAgo] = useState(0)
  const [count, setCount] = useState(0)

  useEffect(() => {
    const fetchData = async () => {
        try {
          const { data } = await supabase
            .from('SportsTalk')
            .select()
            .eq('id', props.id)
            .single();

          const createdAt = new Date(data.created_at);
          const currentTime = new Date();
          const hoursDifference = Math.abs(currentTime - createdAt) / 36e5;
          setHoursAgo(Math.round(hoursDifference));
          setCount(data.upvotes)
        } catch (error) {
            console.error('Error fetching data:', error.message);
        }
    };

    fetchData();
}, [props.id]);



  return (
      <div className="Card">
        <p>Posted {hoursAgo} hours ago</p>

        <Link 
          to={'info/' + props.id} 
          style={{ textDecoration: 'none', color: 'black' }}>
          <h5>{props.title}</h5>
        </Link>

        <p className="content">{props.upvotes} upvotes</p>

      </div>
  );
};

export default Card;
