import React, { useState } from 'react';
import { supabase } from '../client'; // Assuming you have a Supabase client instance set up in '../client'
import Card from './Card'

const SearchBar = ({ onSearch }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = async (e) => {
    e.preventDefault();
    onSearch(searchTerm);
  };

  return (
    <form onSubmit={handleSearch}>
      <input
        type="text"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        placeholder="Search by title..."
      />
      <button type="submit">Search</button>
    </form>
  );
};

const SearchResults = ({ results }) => {
  return (
    <div>
      <h2>Search Results</h2>
      <ul>
        {results.map((post) => (
          <li key={post.id}>{post.title}</li>
        ))}
      </ul>
    </div>
  );
};

const SearchPosts = () => {
  const [searchResults, setSearchResults] = useState([]);

  const searchPosts = async (title) => {
    try {
      const { data, error } = await supabase
        .from('SportsTalk') // Assuming 'posts' is the name of your table in Supabase
        .select('*')
        .ilike('title', `%${title}%`); // Using ilike to perform a case-insensitive search

      if (error) {
        throw error;
      }

      setSearchResults(data || []);
    } catch (error) {
      console.error('Error searching posts:', error.message);
    }
  };

  return (
    <div>
      <SearchBar onSearch={searchPosts} />
      <SearchResults results={searchResults} />
    </div>
  );
};

export default SearchPosts;
