import './App.css';
import React, { useState, useEffect } from 'react';
import { useRoutes } from 'react-router-dom';
import ReadPosts from './pages/ReadPosts';
import CreatePost from './pages/CreatePost';
import EditPost from './pages/EditPost';
import InfoPost from './pages/InfoPost';
import { Link } from 'react-router-dom';
import SearchBar from '../src/components/SearchBar';
import sp from '../src/pages/sportstalk.png';
import { supabase } from '../src/client';

const App = () => {
    const [posts, setPosts] = useState([]);
    const [filteredPosts, setFilteredPosts] = useState([]);

    useEffect(() => {
        const fetchPosts = async () => {
            try {
                const { data } = await supabase
                    .from('SportsTalk')
                    .select()
                    .order('created_at', { ascending: true });

                setPosts(data);
                setFilteredPosts(data);
            } catch (error) {
                console.error('Error fetching data:', error.message);
            }
        };

        fetchPosts();
    }, []);

    const handleSearch = (searchTerm) => {
        if (searchTerm.trim() === '') {
            setFilteredPosts(posts); // If search term is empty, display all posts
        } else {
            const filtered = posts.filter((post) =>
                post.title.toLowerCase().includes(searchTerm.toLowerCase())
            );
            setFilteredPosts(filtered);
        }
    };

    // Sets up routes
    let element = useRoutes([
        {
            path: '/',
            element: <ReadPosts data={filteredPosts} />,
        },
        {
            path: '/info/:id/edit/:id',
            element: <EditPost data={posts} />,
        },
        {
            path: '/new',
            element: <CreatePost />,
        },
        {
            path: '/info/:id',
            element: <InfoPost data={posts} />,
        },
    ]);

    return (
        <div className="App">
            <nav className="navbar">
                <div className="logo">
                    <img src={sp} alt="SportsTalk Logo" />
                </div>
                <div className="route-buttons">
                    <SearchBar onSearch={handleSearch} />
                    <button>
                        <Link to="/" style={{ textDecoration: 'none', color: 'white' }}>
                            <strong>Home</strong>
                        </Link>
                    </button>
                    <button>
                        <Link to="/new" style={{ textDecoration: 'none', color: 'white' }}>
                            <strong>Create New Post</strong>
                        </Link>
                    </button>
                </div>
            </nav>

            <div className="header"></div>
            <div className="body">{element}</div>
        </div>
    );
};

export default App;
